/** @jsxImportSource @emotion/react */
import { css, jsx } from "@emotion/core";
import React, { useContext } from "react";
import { MovieContext } from "../../Context/MovieContext";
import Container from "../GlobalComponents/Container";

const MoviesPagination = () => {
  const { newPage, currentPage, showPagination } = useContext(MovieContext);

  return (
    <div css={styles} className="moviesPagination">
      {showPagination && (
        <Container>
          <React.Fragment>
            <button
              style={{
                cursor: currentPage !== 1 ? "pointer" : "not-allowed",
                background: currentPage !== 1 ? "#3f37c9" : "#303847",
              }}
              onClick={() => newPage("previous")}
            >
              ANTERIOR
            </button>
            <button onClick={() => newPage("next")}>SIGUIENTE</button>
          </React.Fragment>
        </Container>
      )}
    </div>
  );
};

const styles = css`
  width: 100%;
  .container {
    &:nth-child(1) {
      height: 80px;
      display: flex;
      align-items: center;
      justify-content: center;
      button {
        border: none;
        outline: none;
        background: #3f37c9;
        color: #fff;
        font-size: 20px;
        font-weight: 600;
        border-radius: 4px;
        width: 160px;
        padding: 10px 0;
        cursor: pointer;
        user-select: none;
        margin: 0 10px;
        transition: background 500ms ease-in-out;
        &:hover {
          color:black;
          background: #4cc9f0;
        }
      }
    }
  }
`;

export default MoviesPagination;
